-- create "pets" table
CREATE TABLE `pets` (`id` char(36) NOT NULL, `name` varchar(10) NOT NULL, `type` enum('DOG','CAT') NOT NULL, `code` varchar(255) NOT NULL, `age_month` bigint NOT NULL, `is_deleted` bool NOT NULL DEFAULT false, `created_by` varchar(255) NOT NULL, `created_at` timestamp NOT NULL, `updated_by` varchar(255) NULL, `updated_at` timestamp NULL, PRIMARY KEY (`id`), UNIQUE INDEX `code` (`code`), INDEX `pet_name` (`name`), INDEX `pet_type` (`type`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- create "system_parameters" table
CREATE TABLE `system_parameters` (`id` bigint NOT NULL AUTO_INCREMENT, `key` varchar(255) NOT NULL, `value` varchar(255) NOT NULL, `is_deleted` bool NOT NULL DEFAULT false, `created_by` varchar(255) NOT NULL, `created_at` timestamp NOT NULL, `updated_by` varchar(255) NULL, `updated_at` timestamp NULL, PRIMARY KEY (`id`), UNIQUE INDEX `key` (`key`), INDEX `system_parameter_key` (`key`), INDEX `system_parameter_value` (`value`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- create "users" table
CREATE TABLE `users` (`id` char(36) NOT NULL, `name` varchar(10) NOT NULL, `email` varchar(255) NOT NULL, `phone` varchar(255) NOT NULL, `is_deleted` bool NOT NULL DEFAULT false, `created_by` varchar(255) NOT NULL, `created_at` timestamp NOT NULL, `updated_by` varchar(255) NULL, `updated_at` timestamp NULL, PRIMARY KEY (`id`), UNIQUE INDEX `user_email` (`email`), UNIQUE INDEX `user_phone` (`phone`), INDEX `user_email_phone` (`email`, `phone`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
